package Java;

public class countnoofdigits {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		int num=12345612;
		
		System.out.println(num);
		
		
		int count=0;
		
	while(num>0)
	{
		
		num=num/10;
		
		count++;
		
		
	}
		
	System.out.println("number of digits" +count);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		

	}

}
